                case PowerDensityUnit.PicowattPerCubicFoot: return (baseUnitValue/3.531466672148859e1m) / 1e-12m;
                case PowerDensityUnit.PicowattPerCubicInch: return (baseUnitValue/6.102374409473228e4m) / 1e-12m;
